                            {/* Message */}
                            <h2 className="text-2xl md:text-3xl font-bold text-gray-900 mb-3">
                                Oops! Nothing Found
                            </h2>
                            <p className="text-gray-600 mb-8 max-w-md mx-auto">
                                We couldn't find any products matching your search. Try different keywords or browse our categories below.
                            </p>

                            {/* Suggested Categories */}
                            <div className="mb-8">
                                <h3 className="font-semibold text-gray-900 mb-4">Browse Popular Categories</h3>
                                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                                    {suggestedCategories.map((category) => {
                                        const IconComponent = category.icon;
                                        return (
                                            <Link
                                                key={category.name}
                                                href={category.href}
                                                className="p-4 bg-gradient-to-br from-gray-50 to-gray-100 hover:from-teal-50 hover:to-teal-100 rounded-xl transition-all group"
                                            >
                                                <IconComponent className="w-10 h-10 mb-2 text-gray-700 group-hover:text-teal-600 transition-colors" />
                                                <p className="font-medium text-gray-900 group-hover:text-teal-600 transition-colors">
                                                    {category.name}
                                                </p>
                                            </Link>
                                        );
                                    })}
                                </div>
                            </div>

                            {/* Action Buttons */}
                            <div className="flex flex-col sm:flex-row gap-4 justify-center">
                                <Link
                                    href="/shop"
                                    className="inline-flex items-center justify-center gap-2 px-8 py-4 bg-teal-600 hover:bg-teal-700 text-white rounded-full font-semibold transition-colors"
                                >
                                    <ShoppingCart className="w-5 h-5" />
                                    Browse All Products
                                </Link>
                                <Link
                                    href="/deals"
                                    className="inline-flex items-center justify-center gap-2 px-8 py-4 bg-amber-500 hover:bg-amber-600 text-white rounded-full font-semibold transition-colors"
                                >
                                    <Zap className="w-5 h-5" />
                                    View Hot Deals
                                </Link>
                            </div>
                        </motion.div>

                        {/* Trending Products Section */}
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.3 }}
                            className="mt-12"
                        >
                            <div className="flex items-center justify-between mb-6">
                                <h3 className="text-2xl font-bold text-gray-900">You Might Like</h3>
                                <Link href="/shop" className="text-teal-600 hover:text-teal-700 font-medium flex items-center gap-1">
                                    View All
                                    <TrendingUp className="w-4 h-4" />
                                </Link>
                            </div>
                            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                                {allProducts.slice(0, 4).map((product, index) => (
                                    <motion.div
                                        key={product.id}
                                        initial={{ opacity: 0, y: 20 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        transition={{ delay: 0.4 + index * 0.1 }}
                                    >
                                        <Link href={`/products/${product.id}`}>
                                            <div className="bg-white rounded-2xl overflow-hidden shadow-sm hover:shadow-xl transition-all group">
                                                <div className="relative aspect-square overflow-hidden bg-gray-100">
                                                    <Image
                                                        src={product.image}
                                                        alt={product.name}
                                                        fill
                                                        className="object-cover group-hover:scale-110 transition-transform duration-500"
                                                    />
                                                    <div className={`absolute top-3 left-3 ${badgeColors[product.badge]} text-white px-3 py-1 rounded-full text-xs font-bold`}>
                                                        {product.badge}
                                                    </div>
                                                </div>
                                                <div className="p-4">
                                                    <h4 className="font-semibold text-gray-900 mb-2 line-clamp-2">{product.name}</h4>
                                                    <div className="flex items-center justify-between">
                                                        <span className="text-xl font-bold text-gray-900">${product.price}</span>
                                                        <div className="flex items-center gap-1">
                                                            <Star className="w-4 h-4 text-amber-400 fill-amber-400" />
                                                            <span className="text-sm text-gray-600">{product.rating}</span>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </Link>
                                    </motion.div>
                                ))}
                            </div>
                        </motion.div>
                    </div>
                )}
            </div>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { motion } from 'framer-motion';
import { Mail, Lock, Eye, EyeOff } from 'lucide-react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';

export default function SignInPage() {
    const router = useRouter();
    const [showPassword, setShowPassword] = useState(false);
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });

    const handleLogin = (e: React.FormEvent) => {
        e.preventDefault();

        // Mock authentication logic by checking email string
        if (formData.email.toLowerCase().includes('seller')) {
            router.push('/dashboard');
        } else {
            router.push('/account');
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 flex items-center justify-center py-12 px-4">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="max-w-md w-full"
            >
                <div className="text-center mb-8">
                    <h1 className="text-4xl font-bold mb-2">
                        Welcome <span className="italic font-light">Back</span>
                    </h1>
                    <p className="text-gray-600">Sign in to continue shopping</p>
                </div>

                <div className="bg-white rounded-2xl p-8 shadow-lg">
                    <form className="space-y-6" onSubmit={handleLogin}>
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                            <div className="relative">
                                <Mail className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
                                <input
                                    type="email"
                                    value={formData.email}
                                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                    required
                                    placeholder="buyer@example.com or seller@distress.com"
                                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-teal-500"
                                />
                            </div>
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
                            <div className="relative">
                                <Lock className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
                                <input
                                    type={showPassword ? 'text' : 'password'}
                                    value={formData.password}
                                    onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                                    required
                                    placeholder=""
                                    className="w-full pl-10 pr-12 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-teal-500"
                                />
                                <button
                                    type="button"
                                    onClick={() => setShowPassword(!showPassword)}
                                    className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600"
                                >
                                    {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                                </button>
                            </div>
                        </div>

                        <div className="flex items-center justify-between">
                            <label className="flex items-center">
                                <input type="checkbox" className="w-4 h-4 text-teal-600 border-gray-300 rounded focus:ring-teal-500" />
                                <span className="ml-2 text-sm text-gray-600">Remember me</span>
                            </label>
                            <Link href="/forgot-password" className="text-sm text-teal-600 hover:text-teal-700">
                                Forgot password?
                            </Link>
                        </div>

                        <motion.button
                            type="submit"
                            className="w-full bg-teal-600 hover:bg-teal-700 text-white py-3 rounded-full font-semibold transition-colors"
                            whileHover={{ scale: 1.02 }}
                            whileTap={{ scale: 0.98 }}
                        >
                            Sign In
                        </motion.button>
                    </form>

                    <div className="mt-6 text-center">
                        <p className="text-gray-600">
                            Don't have an account?{' '}
                            <Link href="/signup" className="text-teal-600 hover:text-teal-700 font-semibold">
                                Sign Up
                            </Link>
                        </p>
                    </div>
                </div>
            </motion.div>
        </div>
    );
}

